import { ActionTypes } from "../contants/action-types";
const initialState = {
    products: [{
        id: 1,
        title: "dipesh",
        category: "programer"
    }]
}
export const productsReducer = (state = intialState, { type, payload }) => {
    switch (type) {
      case ActionTypes.SET_PRODUCTS:
        return { ...state, products: payload };
      default:
        return state;
    }
  };
